import React, { useState } from "react";
import "./styles/App.css";
import { Container, Paper, Box, Grid, TextField, IconButton, Button, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import DeleteOutlineIcon from "@material-ui/icons/DeleteOutline";
import GridComponent from "./Components/GridComponent";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100vw",
    height: "100vh",
    backgroundColor: theme.palette.grey[200],
    paddingTop: theme.spacing(5),
  },
  inputGroup: {
    marginBottom: theme.spacing(3),
  },
  addFieldButton: {
    display: 'flex',
    justifyContent: 'space-between',
  },
  pageTitle: {
    marginBottom: theme.spacing(5), 
    fontWeight: 'bold',
    fontSize: '2rem',
    color: '#0066cc',
    textAlign: 'center',
  },
}));

function App() {
  const classes = useStyles();
  const layoutTemplate = { name: "", pinicial: "", pfinal: "", size: "" };
  const [layouts, setLayout] = useState([layoutTemplate,]);

  //const addRows = () => (
  //  setLayout([...layouts, layoutTemplate])
 // );

 const addRows = (isDateField) => {
  const newField = isDateField ? { date: new Date() } : layoutTemplate;
  setLayout([...layouts, newField]);
};

  // Função para validar entrada numérica
  const handleNumericInput = (e) => {
    const value = e.target.value;
    // Substituir não números por uma string vazia
    const numericValue = value.replace(/\D/g, '');
    // Atualizar o valor no campo
    e.target.value = numericValue;
  };

  // Atualiza campo na array
  const changeField = (e, index) => {
    const updatedField = layouts.map((field, i) => index === i ?
      { ...field, [e.target.name]: e.target.value } : field);
    setLayout(updatedField);
  };

  // Função para ser usada nos campos de entrada
  const onChange = (e, index) => {
    // Chamando ambas as funções
    handleNumericInput(e);
    changeField(e, index);
  };

  const removeField = (index) => { 
    const filteredFields = [...layouts];
    filteredFields.splice(index, 1);
    setLayout(filteredFields)
  }

  return (
    <Container className={classes.root}>
      <Paper component={Box} p={4}>

        <Typography variant="h4" className={classes.pageTitle}>
          Gerador de Layout
        </Typography>

        {layouts.map((layout, index) => (
          <GridComponent
            key={index}
            layout={layout}
            index={index}
            changeField={changeField}
            onChange={onChange}
            removeField={removeField}
          />
        ))}

        <Grid container spacing={3} className={classes.addFieldButton}>
          <Grid item xs={12} md={3}>
            <Button variant="contained" color="primary" onClick={() => addRows(false)}>Adicionar Campo</Button>
          </Grid>
          <Grid item xs={12} md={3}>
            <Button variant="contained" color="primary" onClick={() => addRows(true)}>
              Campo de Data
            </Button>
          </Grid>
          <Grid item xs={12} md={3} container justify="flex-end">
            <Button variant="contained" color="primary">Criar Layout</Button>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
}

export default App;
