import React, { useState, useEffect } from "react";
import { Grid, TextField, IconButton, Button } from "@material-ui/core";
import EditIcon from "@material-ui/icons/Edit";
import SaveIcon from "@material-ui/icons/Save";
import DeleteOutlineIcon from "@material-ui/icons/DeleteOutline";
import TeamButtons from "./Components/TeamButtons.js";


const AbaUpdate = ({ dadosDoBackEnd }) => {
  const [dados, setDados] = useState({
    nome: "",
    pontoInicial: "",
    pontoFinal: "",
    tamanho: ""
  });

  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    // Aqui você pode fazer uma chamada para o backend para obter os dados e atualizar o estado
    // Por exemplo:
    // fetchDadosDoBackEnd().then((response) => setDados(response));
    // Isso é apenas um exemplo. Certifique-se de implementar de acordo com sua lógica de backend.
  }, []);

  const removeField = (fieldName) => {
    // Criar uma cópia dos dados
    const updatedDados = { ...dados };
    // Remover o campo correspondente
    delete updatedDados[fieldName];
    // Atualizar o estado com os dados atualizados
    setDados(updatedDados);
  };

  const handleEdit = () => {
    setEditMode(!editMode);
  };

  const handleChange = (event, field) => {
    setDados({
      ...dados,
      [field]: event.target.value
    });
  };

  const handleSave = () => {
    // Implemente a lógica para salvar os dados editados
    console.log("Dados salvos:", dados);
    setEditMode(false);
  };

  const addRows = () => {
    const newField = layoutTemplate;
    setLayout([...layouts, newField]);
  };

  return (
    <Grid container spacing={3}>
      <Grid item xs={12} sm={6} md={2}>
        <TextField
          label="Nome"
          variant="outlined"
          fullWidth
          value={dados.nome}
          onChange={(e) => handleChange(e, 'nome')}
          disabled={!editMode}
        />
      </Grid>
      <Grid item xs={12} sm={6} md={2}>
        <TextField
          label="Ponto Inicial"
          variant="outlined"
          fullWidth
          value={dados.pontoInicial}
          onChange={(e) => handleChange(e, 'pontoInicial')}
          disabled={!editMode}
        />
      </Grid>
      <Grid item xs={12} sm={6} md={2}>
        <TextField
          label="Ponto Final"
          variant="outlined"
          fullWidth
          value={dados.pontoFinal}
          onChange={(e) => handleChange(e, 'pontoFinal')}
          disabled={!editMode}
        />
      </Grid>
      <Grid item xs={12} sm={6} md={2}>
        <TextField
          label="Tamanho"
          variant="outlined"
          fullWidth
          value={dados.tamanho}
          onChange={(e) => handleChange(e, 'tamanho')}
          disabled={!editMode}
        />
      </Grid>
      <Grid item xs={10} sm={3} md={1}>
        {editMode ? (
          <IconButton color="primary" onClick={handleSave}>
            <SaveIcon />
          </IconButton>
        ) : (
          <IconButton color="primary" onClick={handleEdit}>
            <EditIcon />
          </IconButton>
        )}
        </Grid>
        <Grid item xs={10} sm={3} md={1}>
        <IconButton color="secondary" onClick={() => removeField('tamanho')}>
          <DeleteOutlineIcon />
        </IconButton>
        <TeamButtons addRows={addRows} />
      </Grid>
    </Grid>
  );
};

export default AbaUpdate;
