import React, { useState } from "react";
import "./styles/App.css";
import { Container, Paper, Box, Grid, TextField, IconButton, Button, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import DeleteOutlineIcon from "@material-ui/icons/DeleteOutline";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100vw",
    height: "100vh",
    backgroundColor: theme.palette.grey[200],
    paddingTop: theme.spacing(5),
  },
  inputGroup: {
    marginBottom: theme.spacing(3),
  },
  addFieldButton: {
    display: 'flex',
    justifyContent: 'space-between',
  },
  pageTitle: {
    marginBottom: theme.spacing(5), 
    fontWeight: 'bold',
    fontSize: '2rem',
    color: '#0066cc',
    textAlign: 'center',
  },
}));

function App() {
  const classes = useStyles();
  const layoutTemplate = { name: "", pinicial: "", pfinal: "", size: "" };
  const [layouts, setLayout] = useState([layoutTemplate,]);

  const addRows = () => (
    setLayout([...layouts, layoutTemplate])
  );

  // Função para validar entrada numérica
  const handleNumericInput = (e) => {
    const value = e.target.value;
    // Substituir não números por uma string vazia
    const numericValue = value.replace(/\D/g, '');
    // Atualizar o valor no campo
    e.target.value = numericValue;
  };

  // Atualiza campo na array
  const changeField = (e, index) => {
    const updatedField = layouts.map((field, i) => index === i ?
      { ...field, [e.target.name]: e.target.value } : field);
    setLayout(updatedField);
  };

  // Função para ser usada nos campos de entrada
  const onChange = (e, index) => {
    // Chamando ambas as funções
    handleNumericInput(e);
    changeField(e, index);
  };

  const removeField = (index) => { 
    const filteredFields = [...layouts];
    filteredFields.splice(index, 1);
    setLayout(filteredFields)
  }

  return (
    <Container className={classes.root}>
      <Paper component={Box} p={4}>

        <Typography variant="h4" className={classes.pageTitle}>
          Gerador de Layout
        </Typography>

        {layouts.map((layout, index) => (
          <Grid container spacing={3} key={index} className={classes.inputGroup}>
            {/* Adicionando condição para renderizar campos de data ou padrão */}
            {layout.date ? (
              <Grid item xs={12} md={12}>
                <TextField
                  label="Data"
                  variant="outlined"
                  fullWidth
                  disabled
                  value={layout.date.toLocaleDateString()}
                />
              </Grid>
            ) : (
              <>
                <Grid item xs={12} md={3}>
                  <TextField
                    label="Nome"
                    placeholder="Nome"
                    variant="outlined"
                    fullWidth
                    name="name"
                    onChange={(e) => onChange(e, index)}
                  />
                </Grid>
                <Grid item xs={12} md={2}>
                  <TextField
                    label="Ponto Inicial"
                    placeholder="Ponto Inicial"
                    variant="outlined"
                    fullWidth
                    name="pinicial"
                    onChange={(e) => onChange(e, index)}
                  />
                </Grid>
                <Grid item xs={12} md={2}>
                  <TextField
                    label="Ponto Final"
                    placeholder="Ponto Final"
                    variant="outlined"
                    fullWidth
                    name="pfinal"
                    onChange={(e) => onChange(e, index)}
                  />
                </Grid>
                <Grid item xs={12} md={2}>
                  <TextField
                    label="Tamanho"
                    placeholder="Tamanho"
                    variant="outlined"
                    fullWidth
                    name="size"
                    onChange={(e) => onChange(e, index)}
                  />
                </Grid>
              </>
            )}

            <Grid item xs={12} md={1}>
              <IconButton color="secondary">
                <DeleteOutlineIcon />
              </IconButton>
            </Grid>
          </Grid>
        ))}

        <Grid container spacing={3} className={classes.addFieldButton}>
          <Grid item xs={12} md={6}>
            <Button variant="contained" onClick={() => addRows(false)}>
              Adicionar Campo Padrão
            </Button>
          </Grid>
          <Grid item xs={12} md={6}>
            <Button variant="contained" color="primary" onClick={() => addRows(true)}>
              Adicionar Campo de Data
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
}

export default App;
