import React, { useState } from "react";
import "./styles/App.css";
import {
  Container,
  Paper,
  Box,
  Grid,
  TextField,
  IconButton,
  Button,
  Typography,
  makeStyles,
} from "@material-ui/core";
import DeleteOutlineIcon from "@material-ui/icons/DeleteOutline";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100vw",
    height: "100vh",
    backgroundColor: theme.palette.grey[300],
    paddingTop: theme.spacing(5),
  },
  inputGroup: {
    marginBottom: theme.spacing(1),
  },
  addFieldButton: {
    display: 'flex',
    justifyContent: 'space-between',
    margin: theme.spacing(2),
  },
  pageTitle: {
    marginBottom: theme.spacing(2),
    fontWeight: 'bold',
    fontSize: '2rem',
    color: theme.palette.primary.main,
    textAlign: 'center',
  },
}));

function App() {
  const classes = useStyles();
  const layoutTemplate = { name: "", pinicial: "", pfinal: "", size: "" };
  const [layouts, setLayout] = useState([layoutTemplate]);

  const addRows = (isDateField) => {
    const newField = isDateField ? { date: new Date() } : layoutTemplate;
    setLayout([...layouts, newField]);
  };

  const handleNumericInput = (e) => {
    const value = e.target.value;
    const numericValue = value.replace(/\D/g, '');
    e.target.value = numericValue;
  };

  const changeField = (e, index) => {
    const updatedField = layouts.map((field, i) => index === i ?
      { ...field, [e.target.name]: e.target.value } : field);
    setLayout(updatedField);
  };

  const onChange = (e, index) => {
    handleNumericInput(e);
    changeField(e, index);
  };

  return (
    <Container className={classes.root}>
      <Paper component={Box} p={4}>
        <Typography variant="h1" className={classes.pageTitle}>
          Gerador de Layout
        </Typography>

        {layouts.map((layout, index) => (
          <Grid container spacing={3} key={index} className={classes.inputGroup}>
            {/* Adicionando condição para renderizar campos de data ou padrão */}
            {layout.date ? (
              <Grid item xs={12} md={12}>
                <TextField
                  label="Data"
                  variant="outlined"
                  fullWidth
                  disabled
                  value={layout.date.toLocaleDateString()}
                />
              </Grid>
            ) : (
              <>
                <Grid item xs={12} md={3}>
                  <TextField
                    label="Nome"
                    placeholder="Nome"
                    variant="outlined"
                    fullWidth
                    name="name"
                    onChange={(e) => onChange(e, index)}
                  />
                </Grid>
                <Grid item xs={12} md={2}>
                  <TextField
                    label="Ponto Inicial"
                    placeholder="Ponto Inicial"
                    variant="outlined"
                    fullWidth
                    name="pinicial"
                    onChange={(e) => onChange(e, index)}
                  />
                </Grid>
                <Grid item xs={12} md={2}>
                  <TextField
                    label="Ponto Final"
                    placeholder="Ponto Final"
                    variant="outlined"
                    fullWidth
                    name="pfinal"
                    onChange={(e) => onChange(e, index)}
                  />
                </Grid>
                <Grid item xs={12} md={2}>
                  <TextField
                    label="Tamanho"
                    placeholder="Tamanho"
                    variant="outlined"
                    fullWidth
                    name="size"
                    onChange={(e) => onChange(e, index)}
                  />
                </Grid>
              </>
            )}

            <Grid item xs={12} md={1}>
              <IconButton color="secondary">
                <DeleteOutlineIcon />
              </IconButton>
            </Grid>
          </Grid>
        ))}

        <Grid container spacing={3} className={classes.addFieldButton}>
          <Grid item xs={12} md={6}>
            {/* Adicionando opção de data ao chamar a função addRows */}
            <Button variant="contained" onClick={() => addRows(false)}>
              Adicionar Campo Padrão
            </Button>
          </Grid>
          <Grid item xs={12} md={6} container justify="flex-end">
            <Button variant="contained" color="primary" onClick={() => addRows(true)}>
              Adicionar Campo de Data
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
}

export default App;
