import React, { useState } from "react";
import "./styles/App.css";
import { Container, Paper, Box, Grid, TextField, IconButton, Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import DeleteOutlineIcon from "@material-ui/icons/DeleteOutline"

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100vw",
    height: "100vh",
    backgroundColor: theme.palette.grey[300],
    paddingTop: theme.spacing(5),
  },
  inputGroup: {
    marginBottom: theme.spacing(1),
  },
  addFieldButton: {
    display: 'flex',
    justifyContent: 'space-between',
  },
}));

function App() {
  const classes = useStyles();
  const layoutTemplate = { name: "", pinicial: "", pfinal: "", size: "" };
  const [layouts, setLayout] = useState([layoutTemplate,]);
  const addRows = () => (
    setLayout([...layouts, layoutTemplate])
  )

  // Função para validar entrada numérica ou atualizar campo na array
  const onChange = (e, index) => {
    const { name, value } = e.target;

    if (name === "pinicial" || name === "pfinal" || name === "size") {
      // Substituir não números por uma string vazia
      const numericValue = value.replace(/\D/g, '');
      // Atualizar o valor no campo
      const updatedField = layouts.map((field, i) => index === i ? { ...field, [name]: numericValue } : field);
      setLayout(updatedField);
    } else {
      const updatedField = layouts.map((field, i) => index === i ? { ...field, [name]: value } : field);
      setLayout(updatedField);
    }
  }

  return (
    <Container className={classes.root}>
      <Paper component={Box} p={4}>
        {layouts.map((layout, index) => (
          <Grid container spacing={3} key={index} className={classes.inputGroup}>
            <Grid item xs={12} md={3}>
              <TextField
                label="Nome"
                placeholder="Nome"
                variant="outlined"
                fullWidth
                name="name"
                onChange={e => onChange(e, index)}
              />
            </Grid>
            <Grid item xs={12} md={2}>
              <TextField
                label="Ponto Inicial"
                placeholder="Ponto Inicial"
                variant="outlined"
                fullWidth
                name="pinicial"
                onChange={e => onChange(e, index)}
              />
            </Grid>
            <Grid item xs={12} md={2}>
              <TextField
                label="Ponto Final"
                placeholder="Ponto Final"
                variant="outlined"
                fullWidth
                name="pfinal"
                onChange={e => onChange(e, index)}
              />
            </Grid>
            <Grid item xs={12} md={2}>
              <TextField
                label="Tamanho"
                placeholder="Tamanho"
                variant="outlined"
                fullWidth
                name="size"
                onChange={e => onChange(e, index)}
              />
            </Grid>
            <Grid item xs={12} md={1}>
              <IconButton color="secondary">
                <DeleteOutlineIcon />
              </IconButton>
            </Grid>
          </Grid>
        ))}
        <Grid container spacing={3} className={classes.addFieldButton}>
          <Grid item xs={12} md={6}>
            <Button variant="contained" onClick={addRows}>Adicionar Campo</Button>
          </Grid>
          <Grid item xs={12} md={6} container justify="flex-end">
            <Button variant="contained" color="primary">Criar Layout</Button>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
}

export default App;
