import React, {useState} from "react";
import "./styles/App.css";
import { Container, Paper, Box, Typography, Grid, TextField, IconButton } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import DeleteOutlineIcon from "@material-ui/icons/DeleteOutline"

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100vw",
    height: "100vh",
    backgroundColor: theme.palette.grey[300],
    paddingTop: theme.spacing(5),
  },
}));

function App() {
  const classes = useStyles();
  const userTemplate = {name:"", pinicial:"", pfinal}

  // Função para validar entrada numérica
  const handleNumericInput = (e) => {
    const value = e.target.value;
    // Substituir não números por uma string vazia
    const numericValue = value.replace(/\D/g, '');
    // Atualizar o valor no campo
    e.target.value = numericValue;
  };



  return (
    <Container className={classes.root}>
      <Paper component={Box} p={4}>
        <Grid container spacing={3}>
          <Grid item xs={12} md={3}>
            <TextField
              label="Nome"
              placeholder="Nome"
              variant="outlined"
              fullWidth
            />
          </Grid>
          <Grid item xs={12} md={2}>
            <TextField
              label="Ponto Inicial"
              placeholder="Ponto Inicial"
              variant="outlined"
              fullWidth
              onChange={handleNumericInput}
            />
          </Grid>
          <Grid item xs={12} md={2}>
            <TextField
              label="Ponto Final"
              placeholder="Ponto Final"
              variant="outlined"
              fullWidth
              onChange={handleNumericInput}
            />
          </Grid>
          <Grid item xs={12} md={2}>
            <TextField
              label="Tamanho"
              placeholder="Tamanho"
              variant="outlined"
              fullWidth
              onChange={handleNumericInput}
            />
          </Grid>
          <Grid item xs={12} md={1}>
            <IconButton color="secondary">
              <DeleteOutlineIcon />
            </IconButton>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
}

export default App;
